import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';



function Favorites() {
  const [favorites, setFavorites] = useState([]);
  const navigate = useNavigate();
  const userId = localStorage.getItem('jwtToken');

  useEffect(() => {
    fetchFavorites();
  }, []);

  const fetchFavorites = async () => {
    try {
      const response = await fetch(`${apiUrl}/api/favorites`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ' + userId,
        },
      });
      if (response.ok) {
        const data = await response.json();
        const itemDetails = await Promise.all(
          data.map(async (favorite) => {
            const itemResponse = await fetch(`${apiUrl}/api/items/${favorite.itemIds[0]}`, {
              method: 'GET',
              headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + userId,
              },
            });
            if (itemResponse.ok) {
              const itemData = await itemResponse.json();
              return { ...favorite, item: itemData };
            } else {
              console.error('Error fetching item details');
              return favorite;
            }
          })
        );
        setFavorites(itemDetails);
      } else {
        console.error('Error fetching favorite items');
        toast.error('Error fetching favorite items');
      }
    } catch (e) {
      console.error('Error fetching favorite items:', e);
      toast.error('Error fetching favorite items');
    }
  };

  const handleViewItem = (itemId) => {
    navigate(`/view-item/${itemId}`);
  };

  return (
    <div className="favorites-page">
      <h1>Your Favorites</h1>
      {favorites.length > 0 ? (
        <div className="favorites-container">
          {favorites.map((favorite) => (
            <div key={favorite._id} className="favorite-item">
              <h3>{favorite.item.name}</h3>
              <button onClick={() => handleViewItem(favorite.item._id)}>View Item</button>
            </div>
          ))}
        </div>
      ) : (
        <p>You have no favorite items.</p>
      )}
      <ToastContainer />
    </div>
  );
}

export default Favorites;
