import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
const apiUrl = "http://localhost:8080";


function LoginSecurity() {
    const role = localStorage.getItem('userRole');
    const token = localStorage.getItem('jwtToken');
    const userId = localStorage.getItem('username');
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");   
    const [firstName , setFirstname] = useState('');
    const [lastName , setLastname] = useState('');
    const [email , setEmail] = useState('');    
    const [gender, setGender] = useState('');
    const [phoneNumber, setPhoneNumber] = useState(''); 
    const [dateOfBirth, setDateOfBirth] = useState('');
    const [maskedPassword, setMaskedPassword] = useState(""); // Masked password for display

    useEffect(() => {
        if (!role) {
            navigate('/login');
            return;
        }
        getCurrentDetails();
    }, []);
    const getCurrentDetails = async() =>{
        try{
            const response = await fetch(`${apiUrl}/api/auth/get-user-details`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': 'Bearer ' + token
                }
            });
            if(response.ok){
                const data = await response.json();
                setUsername(data.username);
                setPassword(data.password);
                setMaskedPassword("*".repeat(password.length)); 
                setFirstname(data.firstName);
                setLastname(data.lastName);
                setEmail(data.email);
                setDateOfBirth(data.dateOfBirth);
                setPhoneNumber(data.phoneNumber);
                setGender(data.gender);
            }else{
                toast.error('Error occurred getting user details');
            }
        }catch(e){
            console.error("Error", e);
            toast.error('Error occurred getting user details');
        }
    }
    return(
        <div className = "loginDetails">
            <h1>DETAILS</h1>
            <div className="details">
                <p>{firstName}</p>
                <p>{lastName}</p>
                <p>{dateOfBirth}</p>
                <p>{phoneNumber}</p>
                <p>{gender}</p>
            </div>
            <div className="login-email-details">
                <h1>EMAIL</h1>
                <p>{email}</p>
                <h3>Edit</h3>
            </div>
            <div className="login-password-details">
                <h1>Password</h1>
                <p>{setMaskedPassword}</p>
            </div>
        </div>
    )
}


export default LoginSecurity;
