import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import '../assets/styles/Favorites.css'; // Import the CSS file

const apiUrl = 'http://localhost:8080';

function Favorites() {
  const [favorites, setFavorites] = useState([]); // array of favorite item details
  const navigate = useNavigate();
  const userId = localStorage.getItem('jwtToken');
  const role = localStorage.getItem('userRole') || 'guest';
  const [guest, isGuest]  = useState(false);
  if(role !== 'guest') {
    useEffect(() => {
        fetchFavorites();
    }, []);
    }else{
        toast.error('Please log in to view favorites');
        navigate('/login');
    }

  const fetchFavorites = async () => {
    try {
      const response = await fetch(`${apiUrl}/api/favorites`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ' + userId,
        },
      });
      if (response.ok) {
        const data = await response.json();
        const favoriteDetails = await Promise.all(
          data.flatMap(favorite => favorite.itemIds.map(fetchFavoriteDetails))
        );
        setFavorites(favoriteDetails);
      } else {
        console.error('Error fetching favorites');
        toast.error("Error fetching favorites");
      }
    } catch (e) {
      console.error('Error fetching favorites:', e);
      toast.error("Error fetching favorites");
    }
  };

  const fetchFavoriteDetails = async (itemId) => {
    try {
      const response = await fetch(`${apiUrl}/api/items/${itemId}`);
      if (response.ok) {
        const data = await response.json();
        return data;
      } else {
        console.error('Error fetching item details');
        toast.error("Error fetching item details");
      }
    } catch (e) {
      console.error('Error fetching item details:', e);
      toast.error("Error fetching item details");
    }
  };

  return (
    <div>
      <h1>Your Favorites</h1>

      <ul className="favorites-container">
        {favorites.map(item => (
          <li key={item.id} className="favorite-item">
            <h3>{item.name}</h3>
            <div className="favorite-item-image">
              <img src={`${apiUrl}${item.image}`} alt={item.name} />
            </div>
            <p>${item.price}</p>
            <div className="button-container">
              <button onClick={() => navigate(`/view-item/${item.id}`)}>View Item</button>
            </div>
          </li>
        ))}
      </ul>
      <ToastContainer />
    </div>
  );
}

export default Favorites;
