import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { FaHeart, FaCartPlus } from 'react-icons/fa'; // Add any additional icons if needed

const apiUrl = 'http://localhost:8080';
function Favorites() {
  const [favorites, setFavorites] = useState([]);
  const [favoriteItems, setFavoriteItems] = useState([]); // New state for item details
  const navigate = useNavigate();
  const userId = localStorage.getItem('jwtToken');

  useEffect(() => {
    fetchFavorites();
  }, []);

  const fetchFavorites = async () => {
    try {
      const response = await fetch(`${apiUrl}/api/favorites`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ' + userId,
        },
      });
      if (response.ok) {
        const data = await response.json();
        setFavorites(data);
        // Fetch details for each favorite item
        const items = await Promise.all(
          data.itemIds.map(async (itemId) => {
            const itemResponse = await fetch(`${apiUrl}/api/items/${itemId}`);
            if (itemResponse.ok) {
              return await itemResponse.json();
            } else {
              console.error(`Error fetching item with ID: ${itemId}`);
              return null;
            }
          })
        );
        setFavoriteItems(items.filter(item => item !== null));
      } else {
        console.error('Error fetching favorite items');
        toast.error('Error fetching favorite items');
      }
    } catch (e) {
      console.error('Error fetching favorite items:', e);
      toast.error('Error fetching favorite items');
    }
  };

  const handleAddToCart = async (item) => {
    await addToCart(item.id, userId);
    toast.success('Item added to cart');
  };

  const handleViewItem = (itemId) => {
    navigate(`/view-item/${itemId}`);
  };

  return (
    <div className="favorites-page">
      <h1>Your Favorites</h1>
      {favoriteItems.length > 0 ? (
        <div className="favorites-container">
          {favoriteItems.map((item) => (
            <div key={item.id} className="favorite-item">
              <img src={`${apiUrl}${item.image}`} alt={item.name} className="favorite-item-image" />
              <h3>{item.name}</h3>
              <p>${item.price}</p>
              <div className="favorite-item-actions">
                <button onClick={() => handleAddToCart(item)}>
                  <FaCartPlus /> Add to Cart
                </button>
                <button onClick={() => handleViewItem(item.id)}>View Item</button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>You have no favorite items.</p>
      )}
      <ToastContainer />
    </div>
  );
}

export default Favorites;
