import React, { createContext, useContext, useState } from "react";
import { useLocation } from "react-router-dom";

const BreadcrumbContext = createContext();

export const useBreadcrumb = () => useContext(BreadcrumbContext);

export const BreadcrumbProvider = ({ children }) => {
  const location = useLocation();
  const [breadcrumbTrail, setBreadcrumbTrail] = useState([{ name: "Home", path: "/" }]);

  const addBreadcrumb = (name, path) => {
    // Avoid duplicates and maintain flow
    setBreadcrumbTrail((prev) => {
      if (prev.find((crumb) => crumb.path === path)) return prev;
      return [...prev, { name, path }];
    });
  };

  const resetBreadcrumb = () => {
    setBreadcrumbTrail([{ name: "Home", path: "/" }]);
  };

  return (
    <BreadcrumbContext.Provider value={{ breadcrumbTrail, addBreadcrumb, resetBreadcrumb }}>
      {children}
    </BreadcrumbContext.Provider>
  );
};
