import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { FaHeart, FaCartPlus } from 'react-icons/fa'; // Add any additional icons if needed

const apiUrl = 'http://localhost:8080';
function Favorites() {
  const [favorites, setFavorites] = useState([]); // array of favorites
  const [favoriteDetails, setFavoriteDetails] = useState(null); 
  const navigate = useNavigate();
  const userId = localStorage.getItem('jwtToken');

  useEffect(() => {
    fetchFavorites();
  }, []);

  const fetchFavorites = async () => {
    try {
      const response = await fetch(`${apiUrl}/api/favorites`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ' + userId,
        },
      });
      if(response.ok){
        const data = await response.json();
        setFavorites(data);
      }else{
        console.error('Error fetching favorites');
        toast.error("Error fetching favorites");
      }
    }catch(e){
        console.error('Error fetching favorites:', e);
        toast.error("Error fetching favorites");
        }
    };
    // this will fetch each favorite full detials - img, desc, name, price
    const fetchFavoriteDetails = async () => {
        try{
            const response = await fetch(`${apiUrl}/api/items/${favorites.itemId}`);
            if(response.ok){
                const data = await response.json();
                setFavoriteDetails(data);
            }else{
                console.error('Error fetching favorite details');
                toast.error('Error fetching favorite details');
            }
        }catch(e){
            console.error('Error fetching favorite details:', e);
            toast.error('Error fetching favorite details');
        }
    };

    return(
        <div>
            <h1>Favorites</h1>
            <ToastContainer />
            <div className="favorites-container">
                {favorites.map((favorite) => (
                    <div key={favorite.id} className="favorite-item">
                        <img src={favoriteDetails.img} alt={favoriteDetails.name} />
                        <h3>{favoriteDetails.name}</h3>
                        <p>{favoriteDetails.desc}</p>
                        <p>${favoriteDetails.price}</p>
                        <button onClick={() => navigate(`/view-item/${favoriteDetails.id}`)}>
                            View Item
                        </button>
                    </div>
                ))}
            </div>
        </div>
    )
}

export default Favorites;
