import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "../assets/styles/ViewItem.css";

const apiUrl = "http://localhost:8080";

function ViewItem() {
  const { id } = useParams();
  const [item, setItem] = useState(null);

  useEffect(() => {
    const fetchItemDetails = async () => {
      try {
        const response = await fetch(`${apiUrl}/api/items/${id}`);
        if (response.ok) {
          const data = await response.json();
          setItem(data);
        } else {
          console.error("Error fetching item details");
        }
      } catch (e) {
        console.error("Error fetching item details:", e);
      }
    };
    fetchItemDetails();
  }, [id]);

  if (!item) {
    return <div>Loading...</div>;
  }

  const addToBag = () => {
    // add item to bag 
    const addToCart = async (item) => {
      try {
          const response = await fetch(`${apiUrl}/api/cart/add-item`, { 
              method: 'POST',
              headers: {
                  'Content-Type': 'application/json',
                  'Authorization': 'Bearer ' + userId
              },
              body: JSON.stringify({ id: item.id }), // Send only the item ID
            });
          if(response.ok){
              toast.success('Item added to cart');
              setAnimatedItemId(item.id); // Trigger animation for the item
              setTimeout(() => setAnimatedItemId(null), 1000); // Clear animation state after 1 second
          } else {
              toast.error('Error adding item to cart');
              console.error('Error adding item to cart');
          }
      } catch (e) {
          console.error("Error adding item to cart:", e);
      }
    };

  }

  return (
    <div className="view-item-page">
      <div className="item-container">
        <div className="item-image">
          <img src={`${apiUrl}${item.image}`} alt={item.name} />
        </div>
        <div className="item-details">
          <h1>{item.name}</h1>
          <p className="item-price">${item.price}</p>
          <div className="item-actions">
            <button className="add-to-bag">Add to Bag</button>
            <button className="add-to-favorites">Add to Favorites</button>
            <button className="share">Share</button>
          </div>
          <div className="item-description">
            <h2>Details</h2>
            <p>{item.description}</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ViewItem;
