import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
const apiUrl = "http://localhost:8080";


function LoginSecurity() {
    const role = localStorage.getItem('userRole');
    const token = localStorage.getItem('jwtToken');
    const userId = localStorage.getItem('username');
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");   
    const [firstName , setFirstname] = useState('');
    const [lastName , setLastname] = useState('');
    const [email , setEmail] = useState('');    
    const [gender, setGender] = useState('');
    const [phoneNumber, setPhoneNumber] = useState(''); 
    const [dateOfBirth, setDateOfBirth] = useState('');
    const [maskedPassword] = useState("********"); // Generic masked password

    useEffect(() => {
        if (!role) {
            navigate('/login');
            return;
        }
        getCurrentDetails();
    }, []);
    const getCurrentDetails = async() =>{
        try{
            const response = await fetch(`${apiUrl}/api/auth/get-user-details`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': 'Bearer ' + token
                }
            });
            if(response.ok){
                const data = await response.json();
                setUsername(data.username);
                setPassword(data.password);
                setFirstname(data.firstName);
                setLastname(data.lastName);
                setEmail(data.email);
                setDateOfBirth(data.dateOfBirth);
                setPhoneNumber(data.phoneNumber);
                setGender(data.gender);
            }else{
                toast.error('Error occurred getting user details');
            }
        }catch(e){
            console.error("Error", e);
            toast.error('Error occurred getting user details');
        }
    }
    return(
        <div className = "loginDetails">
            <h1>DETAILS</h1>
            <div className="details">
                <p>{firstName}</p>
                <p>{lastName}</p>
                <p>{dateOfBirth}</p>
                <p>{phoneNumber}</p>
                <p>{gender}</p>
            </div>
            <div className="login-email-details">
                <h1>EMAIL</h1>
                <p>{email}</p>
                <h3>Edit</h3>
            </div>
            <div className="login-password-details">
                <h1>Password</h1>
                <p>{maskedPassword}</p>
            </div>
            <div className="logout-section">
            <button className="action-button" onClick={() => {
                localStorage.clear();
                toast.success("Logged out successfully!");
                navigate('/login');
            }}>
                LOG ME OUT
                <span className="arrow">→</span>
            </button>
        </div>

        <div className="delete-account-section">
            Delete account 
            <span className="arrow">→</span>
        </div>
    </div>
    )
}


export default LoginSecurity;
