import React from "react";
import AppRoutes from "../routes";
import {useNavigate} from 'react-router-dom';

function LandingPage(){
    const navigate = useNavigate();
    const apiUrl = 'http://localhost:8080';
    const guestLogin = async () => {
        try {
            const response = await fetch(`${apiUrl}/api/auth/guest-login`, {
                method: 'POST',
                headers: { 'Content-type': 'application/json' }
            });
            if(response.ok) {
                const data = await response.json();
                const token = data.token;
                if(token){
                    localStorage.setItem('jwtToken', token);  
                    navigate('/homePage');  
                }else{
                    console.error('Token not found');
                }
            } else {
                const textError = await response.text();
                console.error(`Login failed: ${textError}`);
            }
        } catch (e) {
            console.error("Error", e);
            console.error('Error occurred logging in');
        }
    }
    return(
        <div className="landing-page">
            <h1>Welcome to our store!</h1>
            <h2>Enjoy shopping securely!</h2>
            <button onClick={() => navigate('/register')}>Register</button>
            <button onClick={() => navigate('/login')}>Login</button>
            <button onClick={guestLogin}>Guest Login</button>
        </div>
    );
}

export default LandingPage;
