import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const apiUrl = 'http://localhost:8080';
function Favorites() {
  const [favorites, setFavorites] = useState([]);
  const navigate = useNavigate();
  const userId = localStorage.getItem('jwtToken');

  useEffect(() => {
    fetchFavorites();
  }, []);

  const fetchFavorites = async () => {
    try {
      const response = await fetch(`${apiUrl}/api/favorites`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ' + userId,
        },
      });
      if (response.ok) {
        const data = await response.json();
        setFavorites(data);
      } else {
        console.error('Error fetching favorite items');
        toast.error('Error fetching favorite items');
      }
    } catch (e) {
      console.error('Error fetching favorite items:', e);
      toast.error('Error fetching favorite items');
    }
  };

  const handleViewItem = (itemId) => {
    navigate(`/view-item/${itemId}`);
  };

  return (
    <div className="favorites-page">
      <h1>Your Favorites</h1>
      {favorites.length > 0 ? (
        <div className="favorites-container">
          {favorites.map((favorite) => (
            <div key={favorite._id} className="favorite-item">
              <h3>{favorite.name}</h3>
              <button onClick={() => handleViewItem(favorite.itemIds[0])}>View Item</button>
            </div>
          ))}
        </div>
      ) : (
        <p>You have no favorite items.</p>
      )}
      <ToastContainer />
    </div>
  );
}

export default Favorites;
